        /* Only few modules are applicable for Shadow */
        if (_BCM_SYNC_SUCCESS(rv)) {
            rv = _bcm_esw_vlan_sync(unit);
        }
        if (_BCM_SYNC_SUCCESS(rv)) {
            rv = _bcm_esw_port_sync(unit);
        }
        if (_BCM_SYNC_SUCCESS(rv)) {
            rv = _bcm_esw_link_sync(unit);
        }
