        _BCM_SYNC_MODULE_BACKUP(unit, SOC_SCACHE_DICTIONARY_HANDLE, (&scache_module_ptr),
                                0, 0, backup_size,soc_scache_module_data_backup, rv);
        rv = soc_scache_dictionary_sync(unit);
