        /* Set all COSQs to the same value 
         * - They use the maximum of NUM_MMU_AGE_CYCLES to expire */
        SOC_IF_ERROR_RETURN(WRITE_PKTINTAGINGLIMIT0r(unit, 0));
        SOC_IF_ERROR_RETURN(WRITE_PKTINTAGINGLIMIT1r(unit, 0));

