        val = soc_reg64_field32_get(unit, RTAG7_HASH_CONTROLr, hash_control,
                                    DISABLE_HASH_INNER_IPV4_OVER_GRE_Af);
        if (val) *arg |= BCM_HASH_FIELD0_DISABLE_TUNNEL_IP4_GRE;

