/*
 * Function:
 * bcm_esw_switch_opaque_tag_config_get
 * Purpose:
 * This function reads from hardware the values of opaque tag parameters
 * programmed. Note we read values from OPAQUE_TAG_CONFIG_0 or
 * OPAQUE_TAG_CONFIG_1 register (depending on 'index' argument). That's because
 * all registers in opaque tag reg set are programmed identically in the 'set'
 * API, and OPAQUE_TAG_CONFIG_0, or OPAQUE_TAG_CONFIG_1 has one more configured
 * value (opaque_tag_type) than the other registers.
 * Parameters:
 *     unit              - (IN) BCM device number
 *     index             - (IN) Register set values to read from (0 or 1)
 *     opaque_tag_config - (OUT) Buffer used to fill opaque tag parameters read
 *                               from hardware
 * Returns:
 *     BCM_E_XXX
 * Notes:
 */
int bcm_esw_switch_opaque_tag_config_get(int unit, int index,
        bcm_switch_opaque_tag_params_t *opaque_tag_config)
{
#if defined(BCM_TOMAHAWK3_SUPPORT)
    soc_reg_t reg;
    uint32 regval;

    if ((index < 0) || (index >= BCM_SWITCH_MAX_NUM_OPAQUE_TAGS)) {
        return BCM_E_PARAM;
    }

    if (opaque_tag_config == NULL) {
        return BCM_E_PARAM;
    }

    switch (index) {
        case 0:
            reg = OPAQUE_TAG_CONFIG_0r;
            break;

        case 1:
        default:
            reg = OPAQUE_TAG_CONFIG_1r;
            break;
    }

    SOC_IF_ERROR_RETURN(soc_reg32_get(unit, reg, REG_PORT_ANY, 0, &regval));

    opaque_tag_config->ethertype = soc_reg_field_get(unit, reg, regval,
                                                     ETHERTYPEf);
    opaque_tag_config->tag_size = soc_reg_field_get(unit, reg, regval,
                                                    TAG_SIZEf);
    opaque_tag_config->tag_type = soc_reg_field_get(unit, reg, regval,
                                                    OPAQUE_TAG_TYPEf);
    opaque_tag_config->valid = soc_reg_field_get(unit, reg, regval,
                                                 VALIDf);

    return BCM_E_NONE;
#else /* BCM_TOMAHAWK3_SUPPORT */
    return BCM_E_UNAVAIL;
#endif /* BCM_TOMAHAWK3_SUPPORT */
}

/*
 * Function:
 * bcm_esw_switch_opaque_tag_config_set
 * Purpose:
 * This function programs values of opaque tag parameters provided by
 * application in to hardware register set specified by argument 'index'.
 * Note all registers in the set have identical values (as needed by h/w
 * implementation)
 * Parameters:
 *     unit              - (IN) BCM device number
 *     index             - (IN) Register set to program (0 or 1)
 *     opaque_tag_config - (IN) Buffer containing opaque tag parameters
 * Returns:
 *     BCM_E_XXX
 * Notes:
 */
int bcm_esw_switch_opaque_tag_config_set(int unit, int index,
        bcm_switch_opaque_tag_params_t *opaque_tag_config)
{
#if defined(BCM_TOMAHAWK3_SUPPORT)
    /* Do not change order of registers below. Add any new regs at the end */
    static const soc_reg_t reg_set0[] = {
        OPAQUE_TAG_CONFIG_0r,
        EGR_EFPPARS_OPAQUE_TAG_CONFIG_0r,
        EGR_EPARS_OPAQUE_TAG_CONFIG_0r
    };
    /* Do not change order of registers below. Add any new regs at the end */
    static const soc_reg_t reg_set1[] = {
        OPAQUE_TAG_CONFIG_1r,
        EGR_EFPPARS_OPAQUE_TAG_CONFIG_1r,
        EGR_EPARS_OPAQUE_TAG_CONFIG_1r
    };
    soc_reg_t const *reg;
    soc_reg_t obm_reg_base, obm_reg;
    int i, pipe, num_elems, fld_len;
    uint32 regval;
    soc_info_t *si;

    if ((index < 0) || (index >= BCM_SWITCH_MAX_NUM_OPAQUE_TAGS)) {
        return BCM_E_PARAM;
    }

    if (opaque_tag_config == NULL) {
        return BCM_E_PARAM;
    }

    si = &SOC_INFO(unit);

    switch (index) {
        case 0:
            reg = reg_set0;
            num_elems = COUNTOF(reg_set0);
            obm_reg_base = IDB_OBM0_OPAQUE_TAG_CONFIG_0r;
            break;

        case 1:
        default:
            reg = reg_set1;
            num_elems = COUNTOF(reg_set1);
            obm_reg_base = IDB_OBM0_OPAQUE_TAG_CONFIG_1r;
            break;
    }

    /* We use OPAQUE_TAG_CONFIG_x register; the field lengths are identical for
     * all other registers
     */
    fld_len = soc_reg_field_length(unit, reg[0], OPAQUE_TAG_TYPEf);
    /* Opaque tag type of 0 is not valid for h/w programming */
    if ((opaque_tag_config->tag_type == 0) ||
        (opaque_tag_config->tag_type >= (1 << fld_len))) {
        return BCM_E_CONFIG;
    }

    fld_len = soc_reg_field_length(unit, reg[0], TAG_SIZEf);
    /* Tag size values of 0 and 3 are not legal for h/w programming */
    if ((opaque_tag_config->tag_size == 0) ||
        (opaque_tag_config->tag_size == 3) ||
        (opaque_tag_config->tag_size >= (1 << fld_len))) {
        return BCM_E_CONFIG;
    }

    for (i = 0; i < num_elems; i++) {
        SOC_IF_ERROR_RETURN(soc_reg32_get(unit, reg[i], REG_PORT_ANY, 0,
                                          &regval));
        if (i == 0) {
            /* Write opaque tag type in OPAQUE_TAG_CONFIG_x reg */
            soc_reg_field_set(unit, reg[i], &regval, OPAQUE_TAG_TYPEf,
                              opaque_tag_config->tag_type);
        }

        soc_reg_field_set(unit, reg[i], &regval, ETHERTYPEf,
                          opaque_tag_config->ethertype);
        soc_reg_field_set(unit, reg[i], &regval, TAG_SIZEf,
                          opaque_tag_config->tag_size);
        soc_reg_field_set(unit, reg[i], &regval, VALIDf,
                          opaque_tag_config->valid ? 1 : 0);
        SOC_IF_ERROR_RETURN(soc_reg32_set(unit, reg[i], REG_PORT_ANY, 0,
                                          regval));
    }

    for (pipe = 0; pipe < NUM_PIPE(unit); pipe++) {
        if (SOC_PBMP_IS_NULL(si->pipe_pbm[pipe])) {
            continue;
        }
        obm_reg = SOC_REG_UNIQUE_ACC(unit, obm_reg_base)[pipe];

        /* 4 copies of obm_reg per pipe */
        for (i = 0; i < 4; i++) {
            regval = 0;
            SOC_IF_ERROR_RETURN(soc_reg32_get(unit, obm_reg, REG_PORT_ANY, i,
                                              &regval));
            soc_reg_field_set(unit, obm_reg, &regval, ETHERTYPEf,
                              opaque_tag_config->ethertype);
            soc_reg_field_set(unit, obm_reg, &regval, TAG_SIZEf,
                              opaque_tag_config->tag_size);
            soc_reg_field_set(unit, obm_reg, &regval, VALIDf,
                              opaque_tag_config->valid ? 1 : 0);
            SOC_IF_ERROR_RETURN(soc_reg32_set(unit, obm_reg, REG_PORT_ANY, i,
                                              regval));
        }
    }

    /* Program IDB regs here */

    return BCM_E_NONE;
#else /* BCM_TOMAHAWK3_SUPPORT */
    return BCM_E_UNAVAIL;
#endif /* BCM_TOMAHAWK3_SUPPORT */
}

