#define _BCM_SWITCH_DOSATTACK_HDR                                   \
                (BCM_SWITCH_DOSATTACK_OUTER_HDR |                   \
                 BCM_SWITCH_DOSATTACK_INNER_HDR)

#define _BCM_SWITCH_DOSATTACK_VALID_FLAGS                           \
                (BCM_SWITCH_DOSATTACK_FLOWTRACKER_PRESEL |          \
                 _BCM_SWITCH_DOSATTACK_HDR)

/*
 * Function:
 *      _bcm_esw_switch_dosattack_event_fmt_field_get
 * Purpose:
 *      Get corresponding format and format field for dos attack event.
 * Parameters:
 *      unit                    - (IN) BCM device number
 *      event                   - (IN) Dos attack event
 *      fmt                     - (OUT) Format
 *      fmt_field               - (OUT) Format field
 * Returns:
 *      BCM_E_NONE               - Operation successful
 *      BCM_E_PARAM              - Invalid input parameter value
 */
int
_bcm_esw_switch_dosattack_event_fmt_field_get(
    int unit,
    bcm_switch_dosattack_event_t event,
    soc_format_t *fmt,
    soc_field_t *fmt_field)
{
    switch(event) {
        case bcmSwitchDosAttackEventMACSAEqualMACDA:
            *fmt_field = MACSA_EQ_MACDAf;
            break;

        case bcmSwitchDosAttackEventIpFragOffset:
            *fmt_field = IP_FRAG_OFFSET_VALUE_OF_1f;
            break;

        case bcmSwitchDosAttackEventV6Type0RoutingHdr:
            *fmt_field = TYPE_ZERO_ROUTING_HDR_PRESENTf;
            break;

        case bcmSwitchDosAttackEventV6NonLastFragLT1280:
            *fmt_field = V6_NON_LAST_FRAGMENT_SIZE_TOO_SMALLf;
            break;

        case bcmSwitchDosAttackEventTcpIpFirstFragSmall:
            *fmt_field = TCP_HDR_TOO_SMALLf;
            break;

        case bcmSwitchDosAttackEventTcpOffset:
            *fmt_field = TCP_FRAG_OFFSET_EQ_ONEf;
            break;

        case bcmSwitchDosAttackEventSynFrag:
            *fmt_field = TCP_FLAGS_SYN_EQ_ONE_ACK_EQ_ZERO_AND_SRC_PORT_LT_1024f;
            break;

        case bcmSwitchDosAttackEventFlagZeroSeqZero:
            *fmt_field = TCP_ALL_FLAGS_EQ_ZERO_AND_SEQNUM_EQ_ZEROf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsFUP:
            *fmt_field = TCP_FLAGS_FIN_URG_AND_PSH_EQ_ONE_AND_SEQNUM_EQ_ZEROf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsSF:
            *fmt_field = TCP_FLAGS_FIN_AND_SYN_EQ_ONEf;
            break;

        case bcmSwitchDosAttackEventTcpPortsEqual:
            *fmt_field = TCP_SPORT_EQ_DPORTf;
            break;

        case bcmSwitchDosAttackEventUdpPortsEqual:
            *fmt_field = UDP_SPORT_EQ_DPORTf;
            break;

        case bcmSwitchDosAttackEventIcmp:
            *fmt_field = ICMP_ECHO_PKT_SIZE_TOO_BIGf;
            break;

        case bcmSwitchDosAttackEventIcmpFragments:
            *fmt_field = ICMP_FRAGMENTED_PKTf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsSR:
            *fmt_field = TCP_SYN_RSTf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsSynFirstFrag:
            *fmt_field = TCP_SYN_FIRST_FRAGMENTf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsFinNoAck:
            *fmt_field = TCP_FIN_NO_ACKf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsRstNoAck:
            *fmt_field = TCP_RST_NO_ACKf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsNoSFRA:
            *fmt_field = TCP_NO_SYN_FIN_RST_ACKf;
            break;

        case bcmSwitchDosAttackEventTcpDstPortZero:
            *fmt_field = TCP_DPORT_ZEROf;
            break;

        case bcmSwitchDosAttackEventTcpSrcPortZero:
            *fmt_field = TCP_SPORT_ZEROf;
            break;

        case bcmSwitchDosAttackEventTcpIpDstBcast:
            *fmt_field = TCP_IP_BROADCAST_CHECKf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsZero:
            *fmt_field = TCP_FLAG_ZEROf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsResvdNonZero:
            *fmt_field = TCP_RESERVED_NON_ZEROf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsAck:
            *fmt_field = TCP_ACK_ACKNUMBER_ZEROf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsUrgNoUrgPtr:
            *fmt_field = TCP_URG_NO_URG_POINTERf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsNoUrgWithUrgPtr:
            *fmt_field = TCP_NO_URG_URG_POINTERf;
            break;

        case bcmSwitchDosAttackEventTcpFlagsSynOption:
            *fmt_field = TCP_SYN_WITH_DATA_PAYLOADf;
            break;

        default:
            *fmt = INVALIDfmt;
            *fmt_field = INVALIDf;
            LOG_ERROR(BSL_LS_BCM_COMMON, (BSL_META_U(unit,
                            "Wrong Input arguments\n\r")));
            return BCM_E_PARAM;
    }

    *fmt = HVE_DOS_ATTACK_RESULTS_FORMATfmt;

    return BCM_E_NONE;
}

/*
 * Function:
 *      bcm_esw_switch_dosattack_event_set
 * Purpose:
 *      Configure dos attack events for tracking purpose.
 * Parameters:
 *      unit                    - (IN) BCM device number
 *      flags                   - (IN) flags.
 *      event                   - (IN) Dos attack event
 *      arg                     - (IN) arg
 * Returns:
 *      BCM_E_NONE               - Operation successful
 *      BCM_E_PARAM              - Invalid input parameter value
 *      BCM_E_UNAVAIL            - Feature Not supported
 */
int
bcm_esw_switch_dosattack_event_set(
    int unit,
    uint32 flags,
    bcm_switch_dosattack_event_t event,
    int arg)
{
    int i = 0;
    int rv = BCM_E_NONE;
    uint32 enable = 0;
    uint32 dos_attack_fmt_buf[10];
    uint32 data_buf[SOC_MAX_MEM_FIELD_WORDS] = {0};
    soc_mem_t mem = INVALIDm;
    soc_field_t fmt_field = INVALIDf;
    soc_field_t mem_field[2] = {INVALIDf, INVALIDf};
    soc_format_t fmt = INVALIDfmt;

    if (!soc_feature(unit, soc_feature_broadscan_dos_ctrl)) {
        return BCM_E_UNAVAIL;
    }

    /* Validate inputs */
    if ((flags & ~(_BCM_SWITCH_DOSATTACK_VALID_FLAGS)) ||
        (!(flags & BCM_SWITCH_DOSATTACK_FLOWTRACKER_PRESEL)) ||
        (!(flags & _BCM_SWITCH_DOSATTACK_HDR))) {
        LOG_ERROR(BSL_LS_BCM_COMMON, (BSL_META_U(unit,
                        "Wrong Input arguments\n\r")));
        return BCM_E_PARAM;
    }

    mem = HVE_DOS_ATTACK_MASKm;
    if (flags & BCM_SWITCH_DOSATTACK_OUTER_HDR) {
        mem_field[0] = PARSER1_FIXED_HVE_DOS_ATTACK_MASKf;
    }
    if (flags & BCM_SWITCH_DOSATTACK_INNER_HDR) {
        mem_field[1] = PARSER2_FIXED_HVE_DOS_ATTACK_MASKf;
    }

    /* Get Format and format field */
    rv = _bcm_esw_switch_dosattack_event_fmt_field_get(unit,
                    event, &fmt, &fmt_field);
    if (BCM_FAILURE(rv)) {
        return rv;
    }
    enable = (arg > 0) ? 1 : 0;

    rv = soc_mem_read(unit, mem, MEM_BLOCK_ANY, 0, data_buf);
    if (BCM_FAILURE(rv)) {
        return rv;
    }

    for (i = 0; i < 2; i++) {
        if (mem_field[i] == INVALIDf) {
            continue;
        }
        sal_memset(&dos_attack_fmt_buf[0], 0, 10 * sizeof(uint32));
        if (SOC_MEM_FIELD_VALID(unit, mem, mem_field[i])) {
            soc_mem_field_get(unit, mem, (void *) data_buf,
                    mem_field[i], &dos_attack_fmt_buf[0]);

            soc_format_field32_set(unit, fmt,
                    &dos_attack_fmt_buf[0], fmt_field, enable);

            soc_mem_field_set(unit, mem, (void *) data_buf,
                    mem_field[i], &dos_attack_fmt_buf[0]);
        }
    }
    rv = soc_mem_write(unit, mem, MEM_BLOCK_ALL, 0, data_buf);

    return rv;
}

/*
 * Function:
 *      bcm_esw_switch_dosattack_event_get
 * Purpose:
 *      Get dos attack events for tracking purpose.
 * Parameters:
 *      unit                    - (IN) BCM device number
 *      flags                   - (IN) flags.
 *      event                   - (IN) Dos attack event
 *      arg                     - (OUT) arg
 * Returns:
 *      BCM_E_NONE               - Operation successful
 *      BCM_E_PARAM              - Invalid input parameter value
 *      BCM_E_UNAVAIL            - Feature Not supported
 */
int
bcm_esw_switch_dosattack_event_get(
    int unit,
    uint32 flags,
    bcm_switch_dosattack_event_t event,
    int *arg)
{
    int rv = BCM_E_NONE;
    uint32 enable = 0;
    uint32 dos_attack_fmt_buf[10];
    uint32 data_buf[SOC_MAX_MEM_FIELD_WORDS] = {0};
    uint32 hdr_flags_bits = 0;
    soc_mem_t mem = INVALIDm;
    soc_field_t fmt_field = INVALIDf;
    soc_field_t mem_field = INVALIDf;
    soc_format_t fmt = INVALIDfmt;

    hdr_flags_bits = _shr_popcount(flags & _BCM_SWITCH_DOSATTACK_HDR);

    if (!soc_feature(unit, soc_feature_broadscan_dos_ctrl)) {
        return BCM_E_UNAVAIL;
    }

    /* Validate inputs */
    if ((flags & ~(_BCM_SWITCH_DOSATTACK_VALID_FLAGS)) ||
        (!(flags & BCM_SWITCH_DOSATTACK_FLOWTRACKER_PRESEL)) ||
        (hdr_flags_bits != 1)) {
        LOG_ERROR(BSL_LS_BCM_COMMON, (BSL_META_U(unit,
                        "Wrong Input arguments\n\r")));
        return BCM_E_PARAM;
    }

    mem = HVE_DOS_ATTACK_MASKm;
    if (flags & BCM_SWITCH_DOSATTACK_OUTER_HDR) {
        mem_field = PARSER1_FIXED_HVE_DOS_ATTACK_MASKf;
    } else if (flags & BCM_SWITCH_DOSATTACK_INNER_HDR) {
        mem_field = PARSER2_FIXED_HVE_DOS_ATTACK_MASKf;
    }

    /* Get Format and format field */
    rv = _bcm_esw_switch_dosattack_event_fmt_field_get(unit,
                    event, &fmt, &fmt_field);
    if (BCM_FAILURE(rv)) {
        return rv;
    }

    rv = soc_mem_read(unit, mem, MEM_BLOCK_ANY, 0, data_buf);
    if (BCM_FAILURE(rv)) {
        return rv;
    }
    sal_memset(&dos_attack_fmt_buf[0], 0, 10 * sizeof(uint32));
    if (SOC_MEM_FIELD_VALID(unit, mem, mem_field)) {
        soc_mem_field_get(unit, mem, (void *) data_buf,
                mem_field, &dos_attack_fmt_buf[0]);

        enable = soc_format_field32_get(unit, fmt,
                &dos_attack_fmt_buf[0], fmt_field);
    }

    *arg = enable;

    return rv;
}

