#ifndef _MERLIN7_H__H_
#define _MERLIN7_H__H_

#include <phymod/phymod_definitions.h>
/*Initialize phymod module*/
int merlin7_core_identify(const phymod_core_access_t* core, uint32_t core_id, uint32_t* is_identified);

/*Retrive core information*/
int merlin7_core_info_get(const phymod_core_access_t* core, phymod_core_info_t* info);

/*Set\get lane mapping*/
int merlin7_core_lane_map_set(const phymod_core_access_t* core, const phymod_lane_map_t* lane_map);
int merlin7_core_lane_map_get(const phymod_core_access_t* core, phymod_lane_map_t* lane_map);

/*Reset Core*/
int merlin7_core_reset_set(const phymod_core_access_t* core, phymod_reset_mode_t reset_mode, phymod_reset_direction_t direction);
int merlin7_core_reset_get(const phymod_core_access_t* core, phymod_reset_mode_t reset_mode, phymod_reset_direction_t* direction);

/*Retrive firmware information*/
int merlin7_core_firmware_info_get(const phymod_core_access_t* core, phymod_core_firmware_info_t* fw_info);

/*Start\Stop the sequencer*/
int merlin7_core_pll_sequencer_restart(const phymod_core_access_t* core, uint32_t flags, phymod_sequencer_operation_t operation);

/*Wait for core event*/
int merlin7_core_wait_event(const phymod_core_access_t* core, phymod_core_event_t event, uint32_t timeout);

/* re-tune rx path*/
int merlin7_phy_rx_restart(const phymod_phy_access_t* phy);

/*Set phy polarity*/
int merlin7_phy_polarity_set(const phymod_phy_access_t* phy, const phymod_polarity_t* polarity);
int merlin7_phy_polarity_get(const phymod_phy_access_t* phy, phymod_polarity_t* polarity);

/*Set\Get TX Parameters*/
int merlin7_phy_tx_set(const phymod_phy_access_t* phy, const phymod_tx_t* tx);
int merlin7_phy_tx_get(const phymod_phy_access_t* phy, phymod_tx_t* tx);

/*Request for default TX parameters configuration per media type*/
int merlin7_phy_media_type_tx_get(const phymod_phy_access_t* phy, phymod_media_typed_t media, phymod_tx_t* tx);

/*Set\Get TX override Parameters*/
int merlin7_phy_tx_override_set(const phymod_phy_access_t* phy, const phymod_tx_override_t* tx_override);
int merlin7_phy_tx_override_get(const phymod_phy_access_t* phy, phymod_tx_override_t* tx_override);

/*Set\Get RX Parameters*/
int merlin7_phy_rx_set(const phymod_phy_access_t* phy, const phymod_rx_t* rx);
int merlin7_phy_rx_get(const phymod_phy_access_t* phy, phymod_rx_t* rx);

/*Reset phy*/
int merlin7_phy_reset_set(const phymod_phy_access_t* phy, const phymod_phy_reset_t* reset);
int merlin7_phy_reset_get(const phymod_phy_access_t* phy, phymod_phy_reset_t* reset);

/*Control phy power*/
int merlin7_phy_power_set(const phymod_phy_access_t* phy, const phymod_phy_power_t* power);
int merlin7_phy_power_get(const phymod_phy_access_t* phy, phymod_phy_power_t* power);

/*TX transmission control*/
int merlin7_phy_tx_lane_control_set(const phymod_phy_access_t* phy, phymod_phy_tx_lane_control_t tx_control);
int merlin7_phy_tx_lane_control_get(const phymod_phy_access_t* phy, phymod_phy_tx_lane_control_t* tx_control);

/*Rx control*/
int merlin7_phy_rx_lane_control_set(const phymod_phy_access_t* phy, phymod_phy_rx_lane_control_t rx_control);
int merlin7_phy_rx_lane_control_get(const phymod_phy_access_t* phy, phymod_phy_rx_lane_control_t* rx_control);

/*Set the interface mode, speed and other configuration related to interface.*/
int merlin7_phy_interface_config_set(const phymod_phy_access_t* phy, uint32_t flags, const phymod_phy_inf_config_t* config);
int merlin7_phy_interface_config_get(const phymod_phy_access_t* phy, uint32_t flags, phymod_ref_clk_t ref_clock, phymod_phy_inf_config_t* config);

/*Set\Get CL72*/
int merlin7_phy_cl72_set(const phymod_phy_access_t* phy, uint32_t cl72_en);
int merlin7_phy_cl72_get(const phymod_phy_access_t* phy, uint32_t* cl72_en);

/*Get CL72 status*/
int merlin7_phy_cl72_status_get(const phymod_phy_access_t* phy, phymod_cl72_status_t* status);

/*Core Initialization*/
int merlin7_core_init(const phymod_core_access_t* core, const phymod_core_init_config_t* init_config, const phymod_core_status_t* core_status);

/*Phy Initialization*/
int merlin7_phy_init(const phymod_phy_access_t* phy, const phymod_phy_init_config_t* init_config);

/*Set\get loopback mode*/
int merlin7_phy_loopback_set(const phymod_phy_access_t* phy, phymod_loopback_mode_t loopback, uint32_t enable);
int merlin7_phy_loopback_get(const phymod_phy_access_t* phy, phymod_loopback_mode_t loopback, uint32_t* enable);

/*Get rx pmd locked indication*/
int merlin7_phy_rx_pmd_locked_get(const phymod_phy_access_t* phy, uint32_t* rx_pmd_locked);

/*Get link up status indication*/
int merlin7_phy_link_status_get(const phymod_phy_access_t* phy, uint32_t* link_status);

/*Read phymod register*/
int merlin7_phy_reg_read(const phymod_phy_access_t* phy, uint32_t reg_addr, uint32_t* val);

/*Write phymod register*/
int merlin7_phy_reg_write(const phymod_phy_access_t* phy, uint32_t reg_addr, uint32_t val);

int merlin7_phy_firmware_lane_config_set(const phymod_phy_access_t* phy,
                                        phymod_firmware_lane_config_t fw_config);
int merlin7_phy_firmware_lane_config_get(const phymod_phy_access_t* phy,
                                       phymod_firmware_lane_config_t* fw_config);
int merlin7_phy_firmware_core_config_set(const phymod_phy_access_t* phy,
                                        phymod_firmware_core_config_t fw_config);
int merlin7_phy_firmware_core_config_get(const phymod_phy_access_t* phy,
                                       phymod_firmware_core_config_t* fw_config);
#endif /*_MERLIN7_H_*/
