        LOG_INFO(BSL_LS_BCM_FP,
                 (BSL_META_U(unit,
                             "Test Name : itmh_field_group_traffic, stage : %s.\n"), _BCM_PETRA_FIELD_TEST_STAGE_TO_STRING(stage)));

