              rv = _bcm_td_cosq_index_resolve(unit, fa->param[0], 0,
                   _BCM_TD_COSQ_INDEX_STYLE_UCAST_QUEUE, NULL,
                   &cosq_new, NULL);

              BCM_IF_ERROR_RETURN(rv);
              PolicyCheck(unit, mem, R_COS_INT_PRIf, cosq_new);
